export enum effectVal {
  superEffective = 2,
  neutral = 1,
  notQuiteEffective = 0.5
}

export abstract class Fighter {
  constructor(private name: string, private height: number,
      private weight: number, private attack: number,
      private defense: number, private speed: number,
      private hitPoints: number) {}


  // Setters
  setName(newName: string) {
    this.name = newName;
  }

  setHeight(newHeight: number) {
    this.height = newHeight;
  }

  setWeight(newWeight: number) {
    this.weight = newWeight;
  }

  setAttack(newAttack: number) {
    this.attack = newAttack;
  }

  setDefense(newDefense: number) {
    this.defense = newDefense;
  }

  setSpeed(newSpeed: number) {
    this.speed = newSpeed;
  }

  setHitPoints(newHitPoints: number) {
    this.hitPoints = newHitPoints;
  }

  // Getters
  getName(): string {
    return this.name;
  }

  getHeight(): number {
    return this.height;
  }

  getWeight(): number {
    return this.weight;
  }

  getAttack(): number {
    return this.attack;
  }

  getDefense(): number {
    return this.defense;
  }
  
  getSpeed(): number {
    return this.speed;
  }

  getHitPoints(): number {
    return this.hitPoints;
  }

  abstract talk(): string;
  abstract getEffectiveness(defender: Fighter): effectVal;
};

import { effectVal, Fighter } from './fighter';
import { Marvel } from './marvel';
import { Pokemon } from './pokemon';


export class IronMan extends Marvel {
  constructor() {
    super('Iron Man', 1.85, 130, 60, 70, 55, 85, 'EARTH');
  }

  talk(): string {
    return `I am Iron Man`;
  }

  getEffectiveness(defender: Fighter): effectVal {
    if (defender instanceof Pokemon) return effectVal.notQuiteEffective;

    if (defender instanceof Marvel) {
      switch (defender.getPlanet()) {
        case 'ASGARD': return effectVal.notQuiteEffective;
        case 'EARTH': return effectVal.notQuiteEffective;
        case 'TITAN': return effectVal.superEffective;
        case 'ZenWhoberi': return effectVal.neutral;
      }
    }
    return effectVal.neutral;
  }
};

