export abstract class AudioVisualProduction <GenreType> {
  constructor(private name: string, private releaseDate: number,
      private genre: GenreType, private producer: string,
      private cast: Person[]) {}

  // Setters
  setName(newName: string) {
    this.name = newName;
  }

  setReleaseDate(newDate: number) {
    this.releaseDate = newDate;
  }

  setGenre(newGenre: GenreType) {
    this.genre = newGenre;
  }

  setProducer(newProducer: string) {
    this.producer = newProducer;
  }

  setCast(newCast: Person[]) {
    this.cast = newCast;
  }

  // Getters
  getName() {
    return this.name;
  }

  getReleaseDate() {
    return this.releaseDate;
  }

  getGenre() {
    return this.genre;
  }

  getProducer() {
    return this.producer;
  }

  getCast() {
    return this.cast;
  }

  getReleaseYear(): number {
    if (this.releaseDate.toString().length >= 8) return (this.getReleaseDate() / 10000) | 0;

    return this.releaseDate;
  }

}

import { SearchableByName } from "../interfaces/searchable_by_name";
import { SearchableByYear } from "../interfaces/searchable_by_year";
import { Streamable } from "../interfaces/streamable";

export abstract class BasicStreamableCollection<StreamType> implements Streamable<StreamType>, SearchableByName<StreamType>, SearchableByYear<StreamType> {
  constructor(private collection: StreamType[]) {
    
  }

  // Setters
  setCollection(newCollection: StreamType[]) {
    this.collection = newCollection;
  }

  // Getters
  getCollection() {
    return this.collection;
  }

  abstract searchByYear(year: number): StreamType[];
  abstract searchByName(name: string): StreamType[];
}

import { BasicStreamableCollection } from "./basic_streamable_collection";
import { Movie } from "./movie";


export class MovieCollection extends BasicStreamableCollection<Movie> {
  constructor(collection: Movie[]) {
    super(collection);
  }

  searchByName(name: string): Movie[] {
    const result: Movie[] = this.getCollection().filter((movie) => movie.getName() === name);
    return result;
  }

  searchByYear(year: number): Movie[] {
    const result: Movie[] = this.getCollection().filter((movie) => movie.getReleaseYear() === year);
    return result;
  }
}

